mapper.update:
UPDATE nextjs_shop_cart
SET quantity = #{quantity}, product_option_id = #{productOptionId}
WHERE cart_id = #{cartId} AND user_no = #{userNo}

		UPDATE nextjs_shop_cart
		SET selected = #{selected}
		WHERE user_no= #{userNo}
			AND cart_id IN

            #{cartId}

mapper.delete:
		DELETE FROM nextjs_shop_cart
		WHERE user_no= #{userNo} 
			AND cart_id IN

            #{cartId}

mapper.insert:
INSERT INTO nextjs_shop_user_address(address_name, recipient_name, address_phone, zonecode, address, address_detail, memo, default_address, user_no)
VALUES (
	#{addressName}, #{recipientName}, #{addressPhone}, #{zonecode}, #{address}, #{addressDetail}, #{memo}, #{defaultAddress}, #{userNo}
)

mapper.update:
UPDATE nextjs_shop_user_address
SET default_address = 0
WHERE default_address = 1 AND address_id != #{addressId} AND user_no= #{userNo}

mapper.delete:
UPDATE nextjs_shop_user_address
SET deleted = 1
WHERE address_id = #{addressId} AND user_no= #{userNo}
