mapper.select:
SELECT EXISTS (
    SELECT 1 FROM nextjs_shop_seller
    WHERE seller_id = #{sellerId}
) AS exists_flag

SELECT product_option_id, product_id, add_price, stock, created_at, size, sales_count FROM nextjs_shop_product_option WHERE product_id IN
#{id}
ORDER BY product_id, size

mapper.update:
UPDATE nextjs_shop_product_option
SET add_price = #{addPrice}, stock = #{stock}
WHERE product_option_id = #{productOptionId}

UPDATE nextjs_shop_coupon
SET status = #{status}
WHERE coupon_id = #{couponId}
